import React, { useState, useEffect } from 'react';
import './main.css';
import './components/card.css';
import Card from './components/card';
import Navbar from './components/Navbar'
import Recom from './pages/Recom';
function App() {
  const [animeList, setAnimeList] = useState([]);
  const [recomList, setRecomList] = useState([]);
  const [Query, setQuery] = useState('');
  const [Page, setPage] = useState(1);

  const fetchAnime = async (query, page) => {
    const response = await fetch(`https://api.jikan.moe/v4/anime?q="${query}"&page=${Page}&order_by=title&sort=asc`);
    const data = await response.json();
    console.log(data.data);
    setAnimeList(data.data.slice(0,15));
  };

  const fetchRecom = async (query) => {
    const response = await fetch(`https://api.jikan.moe/v4/recommendations/anime`);
    const data = await response.json();
    console.log(data.data);
    setRecomList(data.data.slice(0,25));
  };
  useEffect(() => {
    fetchRecom();
    // recomList = recomList.slice(0,25);
  }, []);
  console.log(recomList);

  return (
    <div className='app'>

      <Navbar />
      <br />
      {/* <br/>
      <br/> */}
      <div className='search1-box content-wrap'>
        <input className='search-box' value={Query} type={"text"} onChange={e => setQuery(e.target.value)} />
        <input className='button-27' value={"Search"} type={"Button"} onClick={() => fetchAnime(Query, Page)} />
      </div>
      <div className='container'>
        {/* <Recom/> */}
            {
            animeList?.map(anime => (
              <Card className={'anime-card'} title={anime.title} description={anime.synopsis} image={anime.images.jpg.large_image_url} key={anime.mal_id} />
            )
            )}
            {
            recomList?.map(recom => (
              <Card className={'anime-card'} title={recom.entry[0].title} image={recom.entry[0].images.jpg.large_image_url} description={recom.content} key={recom.mal_id} />
            )
            )}
      </div>
    </div>
  );
}

export default App;
