import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import '../main.css';
// import './components/card.css';
// import MY_Navbar from './components/Navbar';
import Recom from './Recom';
import Search from './Search';

function Home() {
    const [Query_A, setQueryA] = useState('');

    const [showComponentA, setShowComponentA] = useState(true);

    const fromHome = true;
    const handleQueryChange = (event) => {
        setQueryA(event.target.value);
      };

    const onClickMe = () => {
        setShowComponentA(prevState => !prevState);
    };

    return (
        <div className='app'>
            {/* <MY_Navbar /> */}

            <br />
            <div className='search1-box content-wrap'>

                <input className='search-box' value={Query_A} type={"text"} onChange={handleQueryChange} />
                <Link to={{
        pathname: "/search",
        search: `?query=${Query_A}`,
        state: { fromHome: true }
    }}>
                <input className='button-27' value={"Search"} type={"Button"} onClick={() => onClickMe()} />
    </Link>

            </div>
            <Recom />
            {showComponentA ? <Recom key={129} /> : <Search query_y={Query_A} key={169} />}
        </div>
    );
}

export default Home;
