import React from 'react';
import './card.css'
const Card = ({ title, description, image }) => {
  return (
    <div className="card">
      <div className='img-cover'>
        <img src={image} alt={title} />
      </div>
      <div className="desc">
        <h2 >{title}</h2>
        <hr style={"width: 8px;"}></hr>
        <p >{description}</p>
      </div>
    </div>
  );
};

export default Card;
