import React, { useState, useEffect } from 'react';
import '../main.css';
import Card from '../components/card';
import _ from "lodash";
const ITEMS_PER_PAGE = 10;

function Recom() {
  const [recomList, setRecomList] = useState([]);
  const [page, setPage] = useState(1);
  // console.log(recomList);
  const fetchRecom = async (query) => {
    const response = await fetch(`https://api.jikan.moe/v4/recommendations/anime?page=1`);
    const data = await response.json();
    console.log(data.data);
    setRecomList(data.data);
  };
  useEffect(() => {
    fetchRecom();
  }, []);
  const debouncedSetPage = _.debounce(setPage, 500);
  return (
    <div onScroll={() => {
      const bottom =
        document.documentElement.scrollHeight -
        document.documentElement.scrollTop ===
        document.documentElement.clientHeight;
      if (bottom) {
        setPage(page + 1);
      }
    }}>
      {recomList.slice(0, page * ITEMS_PER_PAGE).map((item, index) => (
        <div className='container'>
          {/* <Recom/> */}

          {
            recomList?.map((recom, index) => (
              <Card className={'anime-card'} title={recom.entry[0].title} image={recom.entry[0].images.jpg.large_image_url} description={recom.content} key={index} />
            )
            )}
        </div>
      ))}
    </div>
  );
}

export default Recom;
