import React from 'react';
import './card.css'
const Card = ({ title, description, image }) => {
  return (
    <div className="card">
      <div className='img-cover'>
        <div className='overlay'>
          <p className='syp'>{description}</p>
        </div>
        <img src={image} alt={title} />
      </div>
      <div className="desc">
        <h2>{title}</h2>
        {/* <hr></hr> */}
      </div>
    </div>
  );
};

export default Card;
