import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';

import '../main.css';
import MY_Navbar2 from '../components/Navbar_2';
import Recom from './Recom';
import Search from './Search';

function Home() {
    const location = useLocation();
    let len = location.search.length;
    console.log(location.search.slice(7, len));
    const [Query_A, setQueryA] = useState('');
    const [showComponentA, setShowComponentA] = useState(true);

    if (location.state === true) {
        setShowComponentA(!showComponentA);
    }


    const onClickMe = () => {
        setShowComponentA(!showComponentA);
    };

    return (
        <div className='app'>
            <MY_Navbar2 />

            <br />
            <div className='content-wrap'>
            <div className='logo'>
            </div>
            <div className='search1-box'>
                <input className='search-box' value={Query_A} type={"text"} onChange={e => setQueryA(e.target.value)} />
            </div>
                <input className='button-27' value={"Search"} type={"Button"} onClick={() => onClickMe()} />
            </div>
            <Recom />
            {showComponentA ? <Recom key={129} /> : <Search query_y={Query_A} key={169}/>}
        </div>
    );
}

export default Home;
