import React, { useState, useEffect } from 'react';
import '../main.css';
import Card_Component from '../components/card';
import MY_Navbar2 from '../components/Navbar_2';
import ThemeToggleButton from '../components/toggleTheme';
const ItemPerPage = 25;

function Watch() {
    const [Query, setQuery] = useState("Naruto");
    let temp = "";
    const [page, setPage] = useState(1);
    const [animeList, setAnimeList] = useState([]);
    const [TotalEP, setTotalEP] = useState(1);

    const [isDarkMode, setIsDarkMode] = useState(false);

    const handleThemeToggle = () => {
        setIsDarkMode(!isDarkMode);
    };


    const fetchAnime = async (query) => {
        const response = await fetch(`https://gogoanime.consumet.stream/anime-details/${query}`);
        const data = await response.json();
        console.log(data.data);
        setAnimeList(data.data);
        setQuery(temp);
    };

    useEffect(() => {
        fetchAnime(Query);
    }, []);
    return (
        <div className={`app`}>
            <div className={isDarkMode ? 'app light-theme' : 'app dark-theme'}>
                <h1>{animeList.animeTitle}</h1>
                <div className='ep-list'>
                    <ul>
                        {
                            animeList.episodesList.episodeId.map((list, index) => {
                                <li>
                                    {list}
                                </li>
                            })
                        }
                    </ul>
                </div>
            </div>
        </div>
    )

}
export default Watch;
