import React, { useState, useEffect, useRef } from 'react';
import '../main.css';
import Card_Component from '../components/card';
import MY_Navbar2 from '../components/Navbar_2';
function Top_Anime(props) {
    const [recomList, setRecomList] = useState([]);
    const [HasNextPage, setHasNextPage] = useState(true);
    const [page, setPage] = useState(1);
    const [totalPages, setTotalPages] = useState(1);


    const [loading, setLoading] = useState(false);
    const [height, setHeight] = useState(0);
    const elementRef = useRef();


    const fetchAnime = async (page_no) => {
        const response = await fetch(`https://api.consumet.org/anime/gogoanime/top-airing?page=${page_no}`);
        const data = await response.json();
        setHasNextPage(data.hasNextPage);
        console.log("NEXT : " + HasNextPage);
        console.log(data.results);
        setRecomList(data.results);
        setTotalPages(totalPages);
        console.log(`TOTAL PAGE: ${totalPages}`);
    };


    useEffect(() => {
        fetchAnime(1);
    }, []);

    useEffect(() => {
        const handleScroll = () => {
            const windowHeight = window.innerHeight;
            const height = elementRef.current.clientHeight;
            setHeight(height);
            const scrollTop = document.documentElement.scrollTop;
            const offsetHeight = document.documentElement.offsetHeight + height;
            const scrollBottom = scrollTop + windowHeight ;
            console.log(`Scroll P = ${scrollBottom} ${windowHeight} doc height = ${offsetHeight} ${height}`)
            if (scrollBottom >= offsetHeight && !loading) {
                if (HasNextPage) {
                    setPage(page + 1);
                    fetchAnime(page);
                }
            }
        };
        window.addEventListener('scroll', handleScroll);
        return () => window.removeEventListener('scroll', handleScroll);
    }, [loading]);

    return (
        <div className='app'>
            <MY_Navbar2 />
            <div className='container' key={"D-ID"} ref={elementRef}>
                {recomList.map((recom, index) => (
                    <div className='card-here' key={"ID" + index} >
                        <Card_Component theme_mode={props.theme} className={'anime-card'} title={recom.title} image={recom.image} />
                    </div>
                ))}
            </div>
        </div>
    );
}

export default Top_Anime;
