import './App.css';
import { useState, useEffect } from 'react';
import Card from './components/card';
function App() {
  const [AnimeList, SetAnimeList] = useState([]);
  const [Search, setSearch] = useState('Naruto');

  const FetchAnime = async (query) => {
    const data = await fetch(`https://api.jikan.moe/v4/anime?q=${query}&order_by=title&sort=asc&limit=5`)
      .then(res => res.json());
      // .then(data => {
        console.log(data);
        SetAnimeList([data.top.slice(0,5)]);
        console.log(AnimeList);
  };


  const consoleLog = (Ser) => {
    console.log("Anime Search:" + Ser)
  }

  return (
    <div className="App">
      <div>

        <input type={"text"} value={Search} onChange={e => setSearch(e.target.value)} onClick={() => consoleLog(Search)} />
        <input type={"Button"} onClick={() => FetchAnime(Search)} value={"Submit"} />
      </div>
      {AnimeList && AnimeList.length > 0 ? (
        <div className="card-container">
          {AnimeList.map(anime => (
            console.log(anime.title),
            <Card title={anime.title} description={anime.synopsis} image={anime.image_url} />


            // <div className="card">
            //   <img src={anime.image_url} alt={anime.title} />
            //   <h2>{anime.title}</h2>
            //   {/* <p>{anime.synopsis}</p> */}
            // </div>
          ))}
        </div>
      ) : (
        <p>No anime found</p>
      )}
    </div>
  );
}

export default App;
