import React, { useState, useEffect } from 'react';
import '../main.css';
import Card_Component from '../components/card';
const ItemPerPage = 5;
function Recom(props) {
  const [recomList, setRecomList] = useState([]);
  const [HasNextPage, setHasNextPage] = useState(true);
  const [page, setPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);

  // console.log(recomList);
  const fetchRecom = async (query) => {
    const response = await fetch(`https://api.consumet.org/anime/gogoanime/recent-episodes?page=${page}`);
    const data = await response.json();
    setHasNextPage(data.hasNextPage);
    console.log("NEXT : " + hasNextPage);
    console.log(data.results);
    setRecomList(data.results);
    setTotalPages(totalPages);
    console.log(`TOTAL PAGE: ${totalPages}`);
  };

  useEffect(() => {
    fetchRecom();
  }, []);

  return (
    <div onScroll={() => {
        if (page < totalPages && HasNextPage) {
          setPage(page + 1);
        }
    }}>
      {recomList.slice(0, page * ItemPerPage).map((item, index) => (
        <div className='container' key={"D-ID" + index}>

          {
            recomList?.map((recom, index) => (
              <Card_Component theme_mode={props.theme} className={'anime-card'} title={recom.title} image={recom.image} key={"ID" + index} />
            )
            )}
        </div>
      ))}
    </div>
  );
}

export default Recom;
