import React, { useState, useEffect } from 'react';
import './main.css';
import './components/card.css';
import Card from './components/card';
import Navbar from './components/Navbar'
import Recom from './pages/Recom';
function App() {
  const [animeList, setAnimeList] = useState([]);
  const [Query, setQuery] = useState('');
  const [Page, setPage] = useState(1);

  const fetchAnime = async (query, page) => {
    const response = await fetch(`https://api.jikan.moe/v4/anime?q="${query}"&page=${Page}&order_by=title&sort=asc`);
    const data = await response.json();
    console.log(data.data);
    setAnimeList(data.data);
  };

  // const fetchRecom = async (query) => {
  //   const response = await fetch(`https://api.jikan.moe/v4/random/anime?limit=12`);
  //   const data = await response.json();
  //   console.log(data.data);
  //   setAnimeList(data.data);
  // };
  // useEffect(() => {
  //   fetchRecom();
  // }, []);

  return (
    <div className='app'>

      <Navbar />
      <br/>
      {/* <br/>
      <br/> */}
      <div className='search1-box content-wrap'>
        <input className='search-box' value={Query} type={"text"} onChange={e => setQuery(e.target.value)} />
        <input className='button-27' value={"Search"} type={"Button"} onClick={() => fetchAnime(Query, Page)} />
      </div>
      <div className='container'>
        {/* {animeList > 0 ( */}
        {/* <Recom/> */}
        {
          animeList?.map(anime => (
            <Card className={'anime-card'} title={anime.title} description={anime.synopsis} image={anime.images.jpg.large_image_url} key={anime.mal_id} />

          )
          )}
      </div>
    </div>
  );
}

export default App;
