import React, { useState, useEffect } from 'react';
import '../main.css';
import Card from '../components/card';


const [Query, setQuery] = useState('');
const [Page, setPage] = useState(1);
const [animeList, setAnimeList] = useState([]);
function Search() {


    return (
        <div className="container">
            {
                animeList?.map(anime => (
                    <Card className={'anime-card'} title={anime.title} description={anime.synopsis} image={anime.images.jpg.large_image_url} key={anime.mal_id} />
                )
                )}
        </div>
    )
}
const fetchAnime = async (query, page) => {
    const response = await fetch(`https://api.jikan.moe/v4/anime?q="${query}"&page=${Page}&order_by=title&sort=asc`);
    const data = await response.json();
    console.log(data.data);
    setAnimeList(data.data.slice(0, 15));
};
export {fetchAnime, Search as default};
