import { useState, useEffect } from 'react';
import Card from './components/card';
function App() {
  const [Search, setSearch] = useState('');
  const [AnimeList, SetAnimeList] = useState([]);

  const FetchAnime = async (query) => {
    try {
      const temp = await fetch(`https://api.jikan.moe/v4/anime?q=${query}&order_by=title&sort=asc&limit=5`)
        .then(res => res.json());
      console.log(temp);
      SetAnimeList(temp.results);
    } catch (error) {
      console.error(error);
    }
  };


  const consoleLog = (Ser) => {
    console.log("Anime Search:" + Ser)
  }

  return (
    <div className="App">
      <div>

      <input type={"text"} onChange={e => setSearch(e.target.value)} onSubmit={() => consoleLog(Search)}/>
      <input type={"Button"} onClick={() => FetchAnime(Search)} value={"Submit"}/>
      </div>
      {AnimeList.length > 0 && (
        <div className="card-container">
          {AnimeList.map(anime => (
