import React, { useState, useEffect } from 'react';
import './main.css'
import './components/card.css'
import Card from './components/card';
import Navbar from './components/Navbar'
function App() {
  const [animeList, setAnimeList] = useState([]);
  const [Query, setQuery] = useState('Naruto');

  const fetchAnime = async (query) => {
    const response = await fetch(`https://api.jikan.moe/v4/anime?q=${query}&order_by=title&sort=asc&limit=12`);
    const data = await response.json();
    console.log(data.data);
    setAnimeList(data.data);
  };
  useEffect(() => {

    // fetchAnime();
  }, []);

  return (
    <div className='app'>

      <Navbar />
      <input value={Query} type={"text"} onChange={e => setQuery(e.target.value)} />
      <input value={"Search"} type={"Button"} onClick={() => fetchAnime(Query)} />
      <div className='container'>
        {/* {animeList > 0 ( */}
        {
          animeList?.map(anime => (
            <Card title={anime.title} description={anime.synopsis} image={anime.images.jpg.large_image_url} key={anime.mal_id} />

            )
        )}
      </div>
    </div>
  );
}

export default App;
