import React, { useState, useEffect } from 'react';
import './main.css'
import './components/card.css'
import Card from './components/card';
import Navbar from './components/Navbar'
function App() {
  const [animeList, setAnimeList] = useState([]);

  useEffect(() => {
    const fetchAnime = async () => {
      const response = await fetch('https://api.jikan.moe/v4/anime?q=Naruto&order_by=title&sort=asc&limit=12');
      const data = await response.json();
      console.log(data.data);
      setAnimeList(data.data);
    };

    fetchAnime();
  }, []);

  return (
    <div className='app'>

      <Navbar />
      <div className='container'>
        {animeList?.map(anime => (
          <Card title={anime.title} description={anime.synopsis} image={anime.images.jpg.large_image_url} key={anime.mal_id} />

        ))}
      </div>
    </div>
  );
}

export default App;
