import React, { useState, useEffect } from 'react';
import './main.css';
import './components/card.css';
import Card from './components/card';

import Recom from './pages/Recom';
import Search from './pages/Search';
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Navigate,
} from "react-router-dom";

import Movie from './pages/Movie';
import Top_anime from './pages/Top_anime';
import Pop_anime from './pages/Pop_anime';

function App() {
  const [Query_A, setQueryA] = useState('');

  const [showComponentA, setShowComponentA] = useState(true);

  const onClickMe = () => {
    setShowComponentA(!showComponentA);
  };

  return (
    <div className='app'>
      <Router>
        <Routes>
          <Route exact path="/" component={""} />
          <Route path="/" component={Movie} />
          <Route path="/" component={Pop_anime} />
          <Route path="/" component={Top_anime} />
        </Routes>
      </Router>
      <br />
      <div className='search1-box content-wrap'>
        <input className='search-box' value={Query_A} type={"text"} onChange={e => setQueryA(e.target.value)} />
        <input className='button-27' value={"Search"} type={"Button"} onClick={() => onClickMe()} />
      </div>
      {/* <Recom /> */}
      {showComponentA ? <Recom key={129} /> : <Search query_y={Query_A} key={169} />}
    </div>
  );
}

export default App;
