import React, { useState, useEffect } from 'react';
import '../main.css';
import Card from '../components/card';
const ITEMS_PER_PAGE = 10;

const Search = ({ query_y }) => {
    const [Query, setQuery] = useState(query_y);
    const [Page, setPage] = useState(1);
    const [totalPages, setTotalPages] = useState(1);
    const [animeList, setAnimeList] = useState([]);
    const length =  0;

    const fetchAnime = async (query, page) => {
        const response = await fetch(`https://api.jikan.moe/v4/anime?q="${query}"&page=${page}&order_by=title&sort=asc`);
        const data = await response.json();
        console.log(data.data);
        setAnimeList(data.data);
        setTotalPages(data.data.total_pages);
    };

    useEffect(() => {
        fetchAnime(Query, 1);
        length = animeList.length();
      }, []);
    return (
        <div onScroll={() => {
            const bottom =
              document.documentElement.scrollHeight -
              document.documentElement.scrollTop ===
              document.documentElement.clientHeight;
              if (bottom && Page < totalPages) {
                setPage(Page + 1);
              }
          }}>
            {animeList.slice(0, Page * ITEMS_PER_PAGE).map((item, index) => (
        <div className="container">
            {
                animeList?.slice(0, length).map((anime, index) => (
                    <Card className={'anime-card'} title={anime.title} description={anime.synopsis} image={anime.images.jpg.large_image_url} key={index} />
                )
                )}

        </div>
        ))}
        </div>
    )

}
export default Search;
