import React from 'react';
import './Navbar.css';
import { Link } from "react-router-dom";
import { useState, useEffect } from 'react';
import Recom from '../pages/Recom';
import Search from '../pages/Search';
const Navbar = () => {
  const [Query_A, setQueryA] = useState('');

  const [showComponentA, setShowComponentA] = useState(true);

  const onClickMe = () => {
    setShowComponentA(!showComponentA);
  };

  const href = useHref('/');
  const movie = useHref('/movie');
  const popular = useHref('/popular');
  const topanime = useHref('/topanime');
  return (
    <div>

    <nav>
      <ul>
        <li>
          {/* <a href="#">Home</a> */}
          <Link to={href}>Home</Link>
        </li>
        <li>
          <Link to={movie}>Movie</Link>
          {/* <a href="#">Movies</a> */}
        </li>
        <li>
        <Link to={popular}>POPULAR</Link>
          {/* <a href="#">Most Popular</a> */}
        </li>
        <li>
        <Link to={topanime}>Top Airing</Link>
          {/* <a href="#">Top Airing</a> */}
        </li>
      </ul>
    </nav>
    <div className='search1-box content-wrap'>
        <input className='search-box' value={Query_A} type={"text"} onChange={e => setQueryA(e.target.value)} />
        <input className='button-27' value={"Search"} type={"Button"} onClick={() => onClickMe()} />
      </div>
      {/* <Recom /> */}
      {showComponentA ? <Recom key={129} /> : <Search query_y={Query_A} key={169} />}

    </div>
  );
};

export default Navbar;
