import React from 'react';
import './card.css'
const Card = ({props}) => {
// const Card = ({ title, description, image, href_q, theme_mode }) => {
  return (
    <a href={"" + props.href_q} target="_blank" rel="noopener noreferrer" className='a-nodec'>

      <div className={`${ props.theme_mode}`}>
        <div className={`card `}>
          <div className='img-cover'>
            <div className='overlay'>
              <p className='syp'>{ props.description}</p>
            </div>
            <img src={ props.image} alt={ props.title} />
          </div>
          <div className="desc">
            <h2>{ props.title}</h2>
            {/* <hr></hr> */}
          </div>
        </div>
      </div>
    </a>
  );
};

export default Card;
